<?php defined('BASEPATH') OR exit('No direct script access allowed');class MY_form_validation extends CI_Form_validation{    public $my_form;    public $field_labels = array();        public function  __construct($field_labels = array()) {        parent::__construct();        $this->my_form =& get_instance();        $this->field_labels = $field_labels;    }	function valid_url(){		if(!preg_match('/^((http:\/\/www\.)|(www\.)|(http:\/\/))[a-zA-Z0-9._-]+\.[a-zA-Z.]{2,5}$/', $_POST['url'])){			$this->my_form->form_validation->set_message('valid_url',"Entrez un lien valide.");			return false;		}		else{			return true;		}	}	function valid_date(){		$date = $_POST['date'];		list($day, $month, $year) = explode('/', $date);		$result = checkdate($month, $day, $year);		if ($result == true){			return true;		}else{			$this->my_form->form_validation->set_message('valid_date', 'Sélectionnez une date valide.');			return false;		}	}	function valid_time(){		$time = substr($_POST['time'], 0, 5);		list($hour, $minute) = explode(':', $date);		if ($hour >= 0 && $hour <= 23 && $minute >= 0 && $minute <= 59){			return true;		}else{			$this->my_form->form_validation->set_message('valid_time', 'Sélectionnez une heure valide.');			return false;		}	}	function valid_datetime(){		$datetime = $_POST['datetime'];		list($date, $time) = explode(' ', $datetime);		list($day, $month, $year) = explode('/', $date);		$result = checkdate($month, $day, $year);		if ($result == true){			return true;		}else{			$this->my_form->form_validation->set_message('valid_datetime', 'Sélectionnez une date valide.');			return false;		}	}	function valid_drop($val, $field){		if ($_POST[$field] > 0){			return true;		}else{			$this->my_form->form_validation->set_message('valid_drop', 'Vous devez faire un choix.');			return false;		}	}}